<script setup lang="ts">
import { Image } from 'ant-design-vue';

interface Platform {
  id: string;
  name: string;
  logo: string;
  description: string;
}

defineProps<{
  platforms: Platform[];
  selectedPlatform: string;
}>();

const emit = defineEmits<{
  complete: [];
  'update:selectedPlatform': [value: string];
}>();

const handlePlatformSelect = (platformId: string) => {
  emit('update:selectedPlatform', platformId);
};

const handleNext = () => {
  emit('complete');
};
</script>

<template>
  <div class="rounded-lg bg-white p-6 shadow">
    <h3 class="mb-4 text-lg font-medium text-gray-900">选择第三方平台</h3>
    <div
      class="grid grid-cols-1 gap-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4"
    >
      <div
        v-for="platform in platforms"
        :key="platform.id"
        class="cursor-pointer rounded-lg border p-4 transition-all duration-200 hover:shadow-md"
        :class="
          selectedPlatform === platform.id
            ? 'border-primary-500 bg-primary-50'
            : 'border-gray-200'
        "
        @click="handlePlatformSelect(platform.id)"
      >
        <div
          class="mx-auto mb-4 flex h-12 w-12 items-center justify-center rounded-md border border-gray-200 bg-white"
        >
          <Image
            :src="platform.logo"
            :alt="platform.name"
            class="h-8 w-8"
            fallback="data:image/png;base64,"
          />
        </div>
        <div class="text-center">
          <h4 class="text-sm font-medium text-gray-900">
            {{ platform.name }}
          </h4>
          <p class="mt-1 text-xs text-gray-500">
            {{ platform.description }}
          </p>
        </div>
      </div>
    </div>

    <div class="mt-8 flex justify-end">
      <button
        type="button"
        class="bg-primary-600 hover:bg-primary-700 focus-ring-primary-500 inline-flex items-center rounded-md border border-transparent px-4 py-2 text-sm font-medium text-white shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2"
        :disabled="!selectedPlatform"
        @click="handleNext"
      >
        下一步
      </button>
    </div>
  </div>
</template>
